sp_widget.client_script:
function($rootScope,$scope) {
  /* widget controller */
  var c = this;

	c.isSide1 = true;

	c.pageNumber = 0;
	var numberItemsPerPage = c.options.anzahl_karten;
	c.pageCount = Math.ceil(c.data.indexCards.length / numberItemsPerPage);

	c.switchSides = function() {
		console.log('switchSides called.');
		c.isSide1 = !c.isSide1;
		console.log('isSide1 = ' + c.isSide1);
	}

	c.selectCard = function(idx) {
		var id = c.data.indexCards[idx].sys_id;
		$rootScope.cardSysID = id;
		$rootScope.$emit('selectCard', id);
	}

	c.newCard = function() {
		$rootScope.$emit('newCard');
	}

	c.getCards = function() {
		var page = c.pageNumber * numberItemsPerPage;
		var result = c.data.indexCards.slice(page, page + numberItemsPerPage);
		return result;
	}

	c.changePage = function(command) {
		if (command == "increase") {
			if (hasNextPage()) c.pageNumber++;
		} else {
			if (c.pageNumber != 0) c.pageNumber--;
		}
		c.data.cardsToShow = c.getCards();
	}

	hasNextPage = function() {
		var page = c.pageNumber + 1;
		return page < c.pageCount;
	}

	c.data.cardsToShow = c.getCards();

}

sp_widget.script:
(function($rootScope) {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.indexCards = [];

	var activeRow = 0;

	var grIndexCards = new GlideRecord('x_183655_karteikas_index_card');
	grIndexCards.query();

	while(grIndexCards.next()) {

		var indexCard = {};
		$sp.getRecordDisplayValues(indexCard, grIndexCards, 'side_1, side_2, sys_id');

		data.indexCards.push(indexCard);
	}


})();
