sp_widget.client_script:
function($rootScope) {
  /* widget controller */
  var c = this;

	c.isSide1 = true;

	var pageNumber = 0;
	var numberItemsPerPage = 3;

	c.switchSides = function() {
		console.log('switchSides called.');
		c.isSide1 = !c.isSide1;
		console.log('isSide1 = ' + c.isSide1);
	}

	c.selectCard = function(idx) {
		var id = c.data.indexCards[idx].sys_id;
		$rootScope.cardSysID = id;
		$rootScope.$emit('selectCard', id);
	}

	c.getCards = function() {
		return c.data.indexCards.slice(pageNumber, numberItemsPerPage);
	}

	c.changePage = function(command) {
		if (command == "increase") {
			pageNumber++;
		} else {
			pageNumber--;
		}
	}

}

sp_widget.script:
(function($rootScope) {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.indexCards = [];

	var activeRow = 0;

	var grIndexCards = new GlideRecord('x_183655_karteikas_index_card');
	grIndexCards.query();

	while(grIndexCards.next()) {

		var indexCard = {};
		$sp.getRecordDisplayValues(indexCard, grIndexCards, 'side_1, side_2, sys_id');

		data.indexCards.push(indexCard);
	}


})();
