sp_widget.client_script:
function($rootScope) {
  /* widget controller */
  var c = this;

	$rootScope.$on('selectCard', function(event, data){
		console.log('Received event for card ' + $rootScope.cardSysID);
		c.server.get({
			action: 'getCard',
			cardSysID: $rootScope.cardSysID
		}).then(function(r){
			console.log('Loaded card ' + r.data);
			c.data.vorderseite = r.data.card.side_1;
			c.data.rueckseite = r.data.card.side_2;
		});
	})
}

sp_widget.script:
(function() {

	if (input && input.cardSysID) {
		console.log('Server side script: ' + input.cardSysID);
		var card = new GlideRecord('x_183655_karteikas_index_card');
		if (card.get(input.cardSysID)) {
			data.card = {};
			$sp.getRecordDisplayValues(data.card, card, 'side_1, side_2');
			console.log('Server side script card: ' + data.card);
		}
	}

})();
