sp_widget.client_script:
function($rootScope) {
	/* widget controller */
	var c = this;

	resetValues = function() {
		c.solutionReadable = false;
		c.infoVorderseiteVisible = false;
		c.infoRueckseiteVisible = false;
	}

	resetValues();

	$rootScope.$on('selectCard', function(event, data){
		resetValues();
		c.server.get({
			action: 'getCard',
			cardSysID: $rootScope.cardSysID
		}).then(function(r){
			console.log('Loaded card ' + r.data);
			c.data.vorderseite = r.data.card.side_1;
			c.data.rueckseite = r.data.card.side_2;
			c.data.infoVorderseite = r.data.card.info_1;
			c.data.infoRueckseite = r.data.card.info_2;
		});
	})

	c.hideSolution = function(solution) {
		var result = "";
		if (solution) {
			result = "solution".replace(/./g, "*");
		}
		return result;
	}

	c.switchReadable = function() {
		c.solutionReadable = !c.solutionReadable;
	}

	c.getReadableIcon = function() {
		var result = c.solutionReadable ? "glyphicon-eye-close" : "glyphicon-eye-open";
		return result;
	}

	c.getInfoIcon = function(isInfoVisible) {
		return isInfoVisible ? "glyphicon glyphicon-chevron-down" : "glyphicon glyphicon-chevron-up";
	}

	c.switchInfoRueckseiteVisible = function() {
		c.infoRueckseiteVisible = !c.infoRueckseiteVisible;
	}

		c.switchInfoVorderseiteVisible = function() {
		c.infoVorderseiteVisible = !c.infoVorderseiteVisible;
	}


}

sp_widget.script:
(function() {

	if (input && input.cardSysID) {
		console.log('Server side script: ' + input.cardSysID);
		var card = new GlideRecord('x_183655_karteikas_index_card');
		if (card.get(input.cardSysID)) {
			data.card = {};
			$sp.getRecordDisplayValues(data.card, card, 'side_1, side_2, info_1, info_2');
			console.log('Server side script card: ' + data.card);
		}
	}

})();
