sp_widget.client_script:
function($rootScope) {
	/* widget controller */
	var c = this;

	c.isVisible = false;



	$rootScope.$on('newCard', function(event, data){
		c.isVisible = true;
		c.data = {
			vorderseite: "a",
			rueckseite: "b",
			infoVorderseite: "c",
			infoRueckseite: "d"
		};
	})

	c.saveCard = function() {
		c.data.saveCard = true;
		c.server.update();
		c.isVisible = false;
	}




}

sp_widget.script:
(function() {

	if (input && input.cardSysID) {
		console.log('Server side script: ' + input.cardSysID);
		var card = new GlideRecord('x_183655_karteikas_index_card');
		if (card.get(input.cardSysID)) {
			data.card = {};
			$sp.getRecordDisplayValues(data.card, card, 'side_1, side_2, info_1, info_2');
			console.log('Server side script card: ' + data.card);
		}
	}

	if (data.saveCard && data.saveCard == true) {

		var newCard = new GlideRecord('x_183655_karteikas_index_card');
		newCard.initialize();
		$sp.getRecordDisplayValues(newCard, data.card, 'side_1, side_2, info_1, info_2');
		newCard.insert();


	}

})();
